mapper.select:
select u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from system_user u
left join system_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'

	AND u.user_id = #{userId}


	AND u.status = #{status}





			AND u.user_name LIKE '%' || #{userName} || '%'


			AND u.phonenumber LIKE '%' || #{phonenumber} || '%'



			AND u.create_time >= #{params.beginTime}



			AND u.create_time <= #{params.endTime}


			AND (u.dept_id = #{deptId}
			OR u.dept_id IN (
			SELECT t.dept_id
			FROM system_dept t
			WHERE position(#{deptId}::text in t.ancestors) > 0
			))







			AND u.user_name like concat('%', #{userName}, '%')


			AND u.phonenumber like concat('%', #{phonenumber}, '%')


			AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


			AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


			AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM system_dept t WHERE find_in_set(#{deptId}, ancestors) ))




${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from system_user u left join system_dept d on u.dept_id = d.dept_id left join system_user_role ur on u.user_id = ur.user_id left join system_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_id, d.parent_id, d.ancestors, d.dept_name, d.order_num, d.leader, d.status as dept_status, r.role_id, r.role_name, r.role_key, r.role_sort, r.data_scope, r.status as role_status from system_user u left join system_dept d on u.dept_id = d.dept_id left join system_user_role ur on u.user_id = ur.user_id left join system_role r on r.role_id = ur.role_id where u.del_flag = '0'
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND u.status = #{status}
and u.user_id not in
#{userId}

select u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from system_user u left join system_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.nick_name like concat('%', #{nickName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId})
${params.dataScope}

select a.user_id from system_user a INNER JOIN system_user_role b on b.user_id=a.user_id
where a.del_flag=0

	and a.dept_id in

		#{deptId}



	and b.role_id in

		#{deptId}

mapper.update:
update system_user
dept_id = #{deptId}, auth_id = #{authId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
